import { configureStore, createSlice } from '@reduxjs/toolkit'

let stock = createSlice({
  name: "stock",
  initialState: [1, 2],
  reducers: {
    changeStock(state) {
      let news = [...state];
      news.push(news[news.length - 1] + 1)
      return news
    }
  }
}) // reducers 라는 값에 객체를 만들고 그 안에 원하는 이름의 값 변경 함수를 만들고 export

export let { changeStock } = stock.actions; // actions == stock 객체의 reducers가 참고하고 있는 객체

export default configureStore({
  reducer: {
    stock: stock.reducer
  }
})
